mapper.update:
UPDATE T_USER
SET LAST_LOGIN = CURRENT_TIMESTAMP
WHERE USER_ID = #{id}

UPDATE T_USER
SET USER_PW = #{pw, jdbcType=VARCHAR},
    USER_AREA = #{area, jdbcType=VARCHAR}
WHERE USER_ID = #{id, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM T_DETECTION
WHERE DETECT_ID IN

  #{id}

mapper.select:
SELECT * FROM ( SELECT D.DETECT_ID AS det_id, D.REG_DT AS "date", D.DETECT_TYPE AS "type", D.CAMERA_LOC AS "loc", C.LATITUDE AS "latitude", C.LONGITUDE AS "longitude" FROM T_DETECTION D JOIN T_CAMERA C ON D.CAMERA_ID = C.CAMERA_ID
WHERE D.DETECT_TYPE = #{type}
ORDER BY D.REG_DT DESC ) WHERE ROWNUM <= 20

mapper.update:
UPDATE T_DETECTION
SET PROG_STATUS = #{prog}
WHERE DETECT_ID = TO_NUMBER(#{det_id})
